import './cart.css';

const Cart = ({ buyFunc, cartData, setCartData }) => {
    const updatedBuyFunc = (obj) => {
        const idx = cartData.findIndex(item => obj.id === item.id);

        if (idx !== -1) {
            // Товар уже в корзине, увеличиваем количество на 1
            setCartData(prevCartData => {
                const updatedCartData = [...prevCartData];
                updatedCartData[idx].count += 1;
                return updatedCartData;
            });
        } else {
            // Товара нет в корзине, добавляем его
            setCartData(prevCartData => [...prevCartData, { ...obj, count: 1 }]);
        }
    };
    return (
        <section>
            <div className="container">
                <p>
                    <b>Total items:</b> {cartData.length}
                </p>
                <button
                    onClick={() => {
                        setCartData([]);
                    }}
                >
                    Clear Cart
                </button>

                {cartData.map((item) => {
                    return (
                        <div className="cart-item" key={item.id}>
                            <div className="cart-item-col">
                                <img src={item.image} alt="" />
                                <div>
                                    <h4>{item.title}</h4>
                                    <p>{item.description}</p>
                                </div>
                            </div>

                            <div className="cart-item-col">
                                <p>
                                    <button
                                        onClick={() => {
                                            minusCount(item);
                                        }}
                                    >
                                        -
                                    </button>
                                    {item.count}
                                    <button
                                        onClick={() => {
                                            buyFunc(item);
                                        }}
                                    >
                                        +
                                    </button>
                                </p>
                                <p>
                                    <b>Price:</b> ${(item.count * item.price).toFixed(2)}
                                </p>
                                <button
                                    onClick={() => {
                                        deleteProduct(item);
                                    }}
                                >
                                    Delete Product
                                </button>
                            </div>
                        </div>
                    );
                })}
            </div>
        </section>
    );
}

export default Cart;
