import ''

const Cart = ({ buyFunc, cartData, setCartData }) => {
    const minusCount = (obj) => {
        setCartData(prevCartData => {
            const idx = prevCartData.findIndex(item => obj.id === item.id);

            if (obj.count > 1) {
                const updatedCartData = [...prevCartData];
                updatedCartData[idx].count -= 1;
                return updatedCartData;
            } else {
                return prevCartData.filter(item => item.id !== obj.id);
            }
        });
    };

    const deleteProduct = (obj) => {
        setCartData(prevCartData => prevCartData.filter(item => item.id !== obj.id));
    };
    return (
        <section>
            <div className="container">
                <p>
                    <b>Total items:</b> {cartData.length}
                </p>
                <button
                    onClick={() => {
                        setCartData([]);
                    }}
                >
                    Clear Cart
                </button>

                {cartData.map((item) => {
                    return (
                        <div className="cart-item" key={item.id}>
                            <div className="cart-item-col">
                                <img src={item.image} alt="" />
                                <div>
                                    <h4>{item.title}</h4>
                                    <p>{item.description}</p>
                                </div>
                            </div>

                            <div className="cart-item-col">
                                <p>
                                    <button
                                        onClick={() => {
                                            minusCount(item);
                                        }}
                                    >
                                        -
                                    </button>
                                    {item.count}
                                    <button
                                        onClick={() => {
                                            buyFunc(item);
                                        }}
                                    >
                                        +
                                    </button>
                                </p>
                                <p>
                                    <b>Price:</b> ${(item.count * item.price).toFixed(2)}
                                </p>
                                <button
                                    onClick={() => {
                                        deleteProduct(item);
                                    }}
                                >
                                    Delete Product
                                </button>
                            </div>
                        </div>
                    );
                })}
            </div>
        </section>
    );
}

export default Cart;
