import React from 'react';
import './cart.css';

const Cart = ({ buyFunc, cartData, setCartData }) => {
    const minusCount = (obj) => {
        const idx = cartData.findIndex(item => obj.id === item.id);

        if (obj.count > 1) {
            const updatedCartData = [...cartData];
            updatedCartData[idx].count = obj.count - 1;
            setCartData(updatedCartData);
        } else {
            const updatedCartData = cartData.filter(item => item.id !== obj.id);
            setCartData(updatedCartData);
        }
    };

    const deleteProduct = (obj) => {
        const updatedCartData = cartData.filter(item => item.id !== obj.id);
        setCartData(updatedCartData);
    };

    return (
        <section>
            <div className="container">
                <div className="row">
                    {cartData.length === 0 ? (
                        <div className="preloader">
                            <div className="lds-ring">
                                <div></div><div></div><div></div><div></div>
                            </div>
                        </div>
                    ) : (
                        <>
                            <p>
                                <b>Total items:</b> {cartData.length}
                            </p>
                            <button
                                onClick={() => {
                                    setCartData([]);
                                }}
                            >
                                Clear Cart
                            </button>

                            {cartData.map((item) => {
                                return (
                                    <div className="cart-item" key={item.id}>
                                        <div className="cart-item-col">
                                            <img src={item.image} alt="" />
                                            <div>
                                                <h4>{item.title}</h4>
                                                <p>{item.description}</p>
                                            </div>
                                        </div>

                                        <div className="cart-item-col">
                                            <p>
                                                <button
                                                    onClick={() => {
                                                        minusCount(item);
                                                    }}
                                                >
                                                    -
                                                </button>
                                                {item.count}
                                                <button
                                                    onClick={() => {
                                                        buyFunc(item);
                                                    }}
                                                >
                                                    +
                                                </button>
                                            </p>
                                            <p>
                                                <b>Price:</b> ${(item.count * item.price).toFixed(2)}
                                            </p>
                                            <button
                                                onClick={() => {
                                                    deleteProduct(item);
                                                }}
                                            >
                                                Delete Product
                                            </button>
                                        </div>
                                    </div>
                                );
                            })}
                        </>
                    )}
                </div>
            </div>
        </section>
    );
}

export default Cart;
